import { Component } from '@angular/core';

@Component({
  selector: 'app-seller-home',
  templateUrl: './seller-home.component.html',
  styleUrls: ['./seller-home.component.css']
})
export class SellerHomeComponent {


  zoomIn(): void {
    const zoomableElement = document.querySelector('.zoomable') as HTMLElement;
    zoomableElement.style.transform = 'scale(1.2)'; // Increase scale to zoom in (1.2 is 120%)
  }

  resetZoom(): void {
    const zoomableElement = document.querySelector('.zoomable') as HTMLElement;
    if (zoomableElement) {
      zoomableElement.style.transform = 'scale(1)';// Reset scale to its original value (1)
    }
  }

}
