import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { HttpServiceService } from '../services/http-service.service';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
// Import the necessary functions from RxJS
import { Observable, Subscription } from 'rxjs';

@Component({
  selector: 'app-seller-auth',
  templateUrl: './seller-auth.component.html',
  styleUrls: ['./seller-auth.component.css']
})
export class SellerAuthComponent implements OnInit {
  showlogin: boolean = false ;


  constructor( private http:HttpClient , private router:Router){
    console.log("mamali");

  }


  ngOnInit() {
    console.log("santosini");

  }


  signup(data:object)
  {
    this.router.navigate(["seller-home"])
    console.log(data)
    // this.http.post("http://localhost:3000/signup" , data ).subscribe((res:any) =>{
    //    console.log(res);
    // })
  }



  openForms(data: string){
   if(data == "login")
   {
    this.showlogin = true;
   }
   else {
    this.showlogin = false;
   }
  }



  login(data:any){
    console.log(data);

  }
}
<div class="seller-auth" *ngIf="! showlogin">
  <div class="sign-up">
    <h1>Seller Sign Up</h1>

    <form (ngSubmit)="signup(form.value)" #form="ngForm" class="common-form">
      <input class="form-input" type="text" ngModel name="name" placeholder="Enter Name">
      <input class="form-input" type="password" ngModel name="password" placeholder="Enter Password">
      <input class="form-input" type="text" ngModel name="email" placeholder="Enter Email">
      <button class="form-button">SignUp</button>
    </form>

    <div>
     <a (click)="openForms('login')" style="cursor:pointer;"> Already Have Account ?  Login</a>
    </div>

  </div>
</div>


<div class="login-auth" *ngIf="showlogin">
  <div class="sign-up">
    <h1>Seller Login</h1>

    <form (ngSubmit)="login(loginForm.value)" #loginForm="ngForm" class="common-form">
      <div class="form-group">
        <label for="email">Email:</label>
        <input type="email" id="email" name="email" ngModel required >
        <div *ngIf="loginForm.controls.email.touched && loginForm.controls.email.errors?.required" class="error-message">
          Email is required.
        </div>
        <div *ngIf="loginForm.controls.email.touched && loginForm.controls.email.errors?.email" class="error-message">
          Invalid email format.
        </div>
      </div>
    </form>

    <div>
      <a (click)="openForms('signup')" style="cursor:pointer;">Don't Have Any Account ? Singup </a>
     </div>

  </div>




</div>
