import { Component } from '@angular/core';

@Component({
  selector: 'app-seller-home',
  templateUrl: './seller-home.component.html',
  styleUrls: ['./seller-home.component.css']
})
export class SellerHomeComponent {


  onMouseEnter() {
    const zoomableElement = document.querySelector('.zoomable') as HTMLElement;
    const zoomedContent = document.querySelector('.zoomed-content') as HTMLElement;

    if (zoomableElement && zoomedContent) {
      zoomedContent.innerHTML = zoomableElement.innerHTML;
    }
  }

  onMouseLeave() {
    const zoomedContent = document.querySelector('.zoomed-content') as HTMLElement;
    zoomedContent.innerHTML = '';
  }


}
