import { Component } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { NgModule } from '@angular/core';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  options = ['Option 1', 'Option 2', 'Option 3', 'Option 4', 'Option 5'];
  filteredOptions = [...this.options];
  selectedOptions: string[] = [];
  searchValue = '';

  filterOptions() {
    this.filteredOptions = this.options.filter(option =>
      option.toLowerCase().includes(this.searchValue.toLowerCase())
    );
  }

  removeOption(option: string) {
    const index = this.selectedOptions.indexOf(option);
    if (index >= 0) {
      this.selectedOptions.splice(index, 1);
    }
  }

  submit() {
    console.log('Selected Options:', this.selectedOptions);
  }
}
<div>

    <div>

      <label>Selected Options</label>
      <div class="chip-container">
        <div class="chip" *ngFor="let option of selectedOptions">
          {{ option }}
          <span class="close-icon" (click)="removeOption(option)">&times;</span>
        </div>
      </div>
    </div>
    <div>


      <select multiple [(ngModel)]="selectedOptions">
        <div>
          <label>Options</label>
          <input type="text" [(ngModel)]="searchValue" (input)="filterOptions()" placeholder="Search">
        </div>
        <option *ngFor="let option of filteredOptions" [value]="option">{{ option }}</option>
      </select>
    </div>
    <button (click)="submit()">Submit</button>
  </div>
